<script>
jQuery(document).ready(function($) {	
	
		
	jQuery('.flexslider').flexslider({
		animation: "slide",
		slideshow: true,
		slideshowSpeed: "4000",
		animationDuration: "7000",
		directionNav: true,
		animationLoop: true                    
	 });
	
     // Handle resizing videos on the video page
	var allVideos  = jQuery(".videoBox iframe[src^='http://player.vimeo.com']");
     // Handle resizing maps on the contact page
	var allMaps  = jQuery(".map iframe[src^='https://maps.google.co.uk']");
	
	// Figure out and save aspect ratio for each video
	allVideos.each(function() {	
		jQuery(this)
		.data('aspectRatio', this.height / this.width)
		// and remove the hard coded width/height
		.removeAttr('height')
		.removeAttr('width');
	});
	// Figure out and save aspect ratio for each map
	allMaps.each(function() {	
		jQuery(this)
		.data('aspectRatio', this.height / this.width)
		// and remove the hard coded width/height
		.removeAttr('height')
		.removeAttr('width');
	});
	
	// When the window is resized
	jQuery(window).resize(function() {
		var newWidth = jQuery('.videoBox').width();
		
		// Resize all videos according to their own aspect ratio
		allVideos.each(function() {
		
		var el = jQuery(this);
		el
		.width(newWidth)
		.height(newWidth * el.data('aspectRatio'));
		});
		
		newWidth = jQuery('.map').width();
		
		// Resize all maps according to their own aspect ratio
		allMaps.each(function() {
		
		var el = jQuery(this);
		el
		.width(newWidth)
		.height(newWidth * el.data('aspectRatio'));
		});
		// Kick off one resize to fix all videos on page load
	}).resize();
	
	// IE6 & 7 dont understand placeholders
	jQuery('input[placeholder],textarea[placeholder]').each(function(){  
		var input = jQuery(this);        
		jQuery(input).val(input.attr('placeholder'));
					
		jQuery(input).focus(function(){
			if (input.val() == input.attr('placeholder')) {
			   input.val('');
			}
		});
			
		jQuery(input).blur(function(){
		   if (input.val() == '' || input.val() == input.attr('placeholder')) {
			   input.val(input.attr('placeholder'));
		   }
		});
	});
	
	});
</script>


